mapper.select:
SELECT tel.id, tel.task_info_id AS taskInfoId, tel.executor
, tel.excute_status AS excuteStatus, tel.create_time AS createTime
, ti.name AS taskName, ti.platform, ti.module, ti.task_type AS taskType
, li.name AS linkName, li.link
, ci.title AS contentTitle, ci.content
FROM task_excute_log tel
LEFT JOIN task_info ti ON tel.task_info_id = ti.id
LEFT JOIN link_info li ON tel.task_info_id = li.task_info_id
LEFT JOIN content_info ci ON tel.task_info_id = ci.task_info_id
WHERE 1=1

    AND ti.platform = #{params.platform}


    AND ti.task_type = #{params.taskType}


    AND ti.name = #{params.taskName}


    AND tel.excute_status = #{params.excuteStatus}


    AND tel.executor = #{params.executor}


    AND tel.create_time  >= #{params.createStartDate} and tel.create_time <= #{params.createEndDate}
